// components/AdminPanel.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useTable } from "react-table";

const AdminPanel = () => {
  const [data, setData] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    phone: "",
    seva: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("/api/users");
        setData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error.message);
      }
    };

    fetchData();
  }, []);

  const columns = [
    { Header: "ID", accessor: "_id" },
    { Header: "Name", accessor: "name" },
    { Header: "Phone", accessor: "phone" },
    { Header: "Seva", accessor: "seva" },
  ];

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({ columns, data });

  const handleFormChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send a POST request to the backend API endpoint to add data
      const response = await axios.post("/api/addData", formData);
      console.log("Data added successfully:", response.data);

      // Fetch updated data after adding a new entry
      const updatedData = await axios.get("/api/users");
      setData(updatedData.data);

      // Clear the form data
      setFormData({
        name: "",
        phone: "",
        seva: "",
      });
    } catch (error) {
      console.error("Error adding data:", error.message);
    }
  };


  return (
    <div>
      <h1>User Data</h1>
      <table {...getTableProps()} style={{ width: "100%" }}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                {row.cells.map((cell) => (
                  <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>

      {/* Form for adding new data */}
      <form onSubmit={handleFormSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleFormChange}
          />
        </label>
        <label>
          Phone:
          <input
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleFormChange}
          />
        </label>
        <label>
          Seva:
          <input
            type="text"
            name="seva"
            value={formData.seva}
            onChange={handleFormChange}
          />
        </label>
        <button type="submit">Add Data</button>
      </form>
    </div>
  );
};

export default AdminPanel;
